int fibonacci(int n) {
    if (n == 0) {
        return 0; // Base case: the 0th Fibonacci number is 0
    }
    if (n == 1) {
        return 1; // Base case: the 1st Fibonacci number is 1
    }
    // Recursive case: sum of the two preceding Fibonacci numbers
    return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
    int n;
    cout << "Enter a non-negative integer to find its Fibonacci number: ";
    cin >> n;

    // Validate input to ensure non-negative integer
    if (n < 0) {
        cout << "Please enter a non-negative integer." << endl;
        return 1;
    }

    int result = fibonacci(n);
    cout << "The " << n << "th Fibonacci number is " << result << "." << endl;
    return 0;
}

